import React ,{useState} from 'react'
import "./ExpenseForm.css"

const ExpernseForm = () =>{

    const [enteredTitle, setEnteredTitle]= useState('')
    const [enteredAmount, setEnteredAmount]= useState('')
    const [enteredDate, setEnteredDate]= useState('')

    const titleChangeHandler= (e) =>{
        setEnteredTitle(e.target.value)
    }
    const amountChangeHandler=(e) =>{
        setEnteredAmount(e.target.value)

    }
    const dateChangeHandler=(e) =>{
        setEnteredDate(e.target.value)
    }
   const SubmitHandler=(e)=>{
       e.preventDefault();


       const expenseData={
           title : enteredTitle,
           amount:enteredAmount,
           date:new Date(enteredDate)
       }

       console.log(expenseData)
       setEnteredTitle('')
       setEnteredAmount('')
       setEnteredDate('')
   }

    return <form onSubmit={SubmitHandler}>
    <div className="new-expense__controls">

    <div className='new-expense__control'>
    <label>Title</label>
    <input type="text" value={enteredTitle} onChange={titleChangeHandler}></input>   
    </div>
    <div className='new-expense__control'>
    <label>Amount</label>
    <input 
    type="number" min="0.01" step="0.01" value={enteredAmount} onChange={amountChangeHandler}></input>   
    </div>
    <div className='new-expense__control'>
    <label>Date</label>
    <input type="date" min="2019-01-01" max="2022-12-31" value={enteredDate} onChange={dateChangeHandler}></input>   
    </div>
    
    </div>
    <div className='new=expense__actions'> 
     <button type='submit' >Add Expense</button>
    </div>
    </form>  
    

    
}

export default ExpernseForm;
